const keywords = [
  'product', 'buy', 'musthave', 'wishlist', 'finds', 'review', 'unboxing', 'haul',
  'amazon', 'amazonfinds', 'amazonmusthaves', 'tiktokmademebuyit', 'tiktokamazonfinds',
  'bestbuy', 'topfinds', 'buynow', 'dealsoftheday', 'aliexpress', 'aliexpressfinds',
  'homefinds', 'kitchenfinds', 'organizerfinds', 'skincarefinds', 'beautyfinds', 'gymfinds',
  'cleaningtiktok', 'shopeefinds', 'viralproduct', 'testedontiktok', 'beforeandafter',
  'recommendation', 'worthit'
];

const validItems = items.filter(i => typeof i.json === 'object' && i.json !== null);

const filtered = validItems.filter(item => {
  const text = item.json.text?.toLowerCase() || '';
  const hashtags = item.json.hashtags?.map(h => h.name.toLowerCase()) || [];
  const foundInText = keywords.some(word => text.includes(word));
  const foundInHashtags = keywords.some(word => hashtags.includes(word));
  return foundInText || foundInHashtags;
});

return filtered;

